const root=document.documentElement,calcRange=(e,o,t)=>t*(o-e)+e;document.addEventListener("scroll",(e=>{const o=calcRange(0,360,window.scrollY/document.body.clientHeight);root.style.setProperty("--color-accent-hue",Math.floor(o));const t=(calcRange(0,360,window.scrollY/document.body.clientHeight)+180)%360;root.style.setProperty("--color-accent-hue-inverse",Math.floor(t)),console.log({range:o,rangeInverted:t})}));